import { HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { camelCase, result } from 'lodash-es';
import { Subject } from 'rxjs';
import { environment } from '../../../environments/environment';
@Injectable({
  providedIn: 'root',
})
export class ApiService {
  localUserKey = camelCase(environment.apiUrl.split('//')[1]);
  public configLoadDone$ = new Subject();

  constructor() {}

  get apiUrl(): string {
    return environment.apiUrl;
  }

  get httpOptionsOfCommon(): any {
    const httpOptions = {
      headers: new HttpHeaders({
        Accept: 'application/json',
        'Content-Type': 'application/json',
      }),
    };
    return httpOptions;
  }

  optionsWithCookieAndToken(csrfToken: string): any {
    const httpOptions = {
      headers: new HttpHeaders({
        Accept: 'application/vnd.api+json',
        'Content-Type': 'application/vnd.api+json',
        'X-CSRF-Token': csrfToken,
      }),
      withCredentials: true,
    };
    return httpOptions;
  }

  getParams(obj: any, key: string): any {
    return obj.params && obj.params[key];
  }

  getDeepValue(obj: any, path: string): any {
    return result(obj, path);
  }
}

import { Inject, Injectable } from '@angular/core';
import { DOCUMENT } from '@angular/common';
import { action, observable, computed } from 'mobx-angular';
import { HttpClient } from '@angular/common/http';
import { environment } from '../../../environments/environment';
import { LocalStorageService } from 'ngx-webstorage';
import { IAppConfig, ICoreConfig, IPage } from './IAppConfig';
import { of, Subject } from 'rxjs';
import { TagsService } from '@core/service/tags.service';
import { version } from '../../../../package.json';
import { isArray } from 'lodash-es';
import { ApiService } from '@core/service/api.service';
import { ScreenState } from '@core/mobx/screen/ScreenState';
import { tap } from 'rxjs/operators';

const initPage = {
  title: '',
  body: [],
};
@Injectable({
  providedIn: 'root',
})
export class AppState {
  private readonly MODE = 'themeMode';
  @observable private state: IAppConfig = {
    defTheme: 'light-theme',
    config: null,
    page: initPage,
  };

  public switchChange$ = new Subject();
  constructor(
    @Inject(DOCUMENT) private document: Document,
    private http: HttpClient,
    private storage: LocalStorageService,
    private tagsService: TagsService,
    public apiService: ApiService,
    private screenState: ScreenState
  ) {}

  @computed get config(): any {
    return this.state && this.state.config;
  }

  @computed get theme(): any {
    return this.state && this.state.defTheme;
  }

  @computed get meta(): any {
    return this.state.page && this.state.page.meta;
  }

  @computed get pageConfig(): any {
    return this.state.page && this.state.page.config;
  }

  @computed get title(): any {
    return this.state.page && this.state.page.title;
  }

  @computed get content(): any[] {
    return this.state.page && this.state.page.body;
  }

  get version(): string {
    return version;
  }

  get defaultThumb(): string {
    return (
      (this.state.config && this.state.config?.defaultThumb) ||
      '/assets/images/default.png'
    );
  }

  setBodyClasses(theme: string): void {
    const body = this.document.getElementsByTagName('body')[0];
    body.classList.add(theme);
  }

  updatePage(pageValue: IPage): void {
    if (isArray(pageValue)) {
      return;
    }
    this.state.page = pageValue;
    this.tagsService.updateTages(pageValue);
    this.screenState.scroll$.next();
  }

  setPageNotFound(notFound: string): void {
    this.tagsService.setTitle('404 not found!');
    this.http.get<any>(notFound).subscribe((pageValue: IPage) => {
      this.updatePage(pageValue);
    });
  }

  get apiPath(): string {
    const location = this.document.location;
    const path = location.pathname;
    const search = location.search;
    const allowKey = ['version', 'origin', 'category', 'preview'];
    if (
      allowKey.some((key) => {
        return search.indexOf(key) > 0;
      })
    ) {
      const params = search.split('?')[1];
      return `${path}&${params}`;
    } else {
      return path;
    }
  }

  @action
  switchTheme(theme: string): void {
    const body = this.document.getElementsByTagName('body')[0];
    body.removeAttribute('class');
    body.classList.add(theme);
    this.switchChange$.next(theme);
    this.state.defTheme = theme;
    this.storage.store(this.MODE, theme);
  }

  @action
  public loadConfig(coreConfig: object): any {
    if (environment.production) {
      return this.http
        .get(`${environment.apiUrl}/api/v1/config?content=/core/base`)
        .pipe(
          tap((config: any) => {
            Object.assign(coreConfig, config);
          })
        )
        .toPromise()
        .then(
          (config: ICoreConfig) => {
            this.state.config = config;
            this.apiService.configLoadDone$.next(true);
            this.initTheme();
          },
          (error) => {
            console.log(error);
            console.log('base json not found!');
          }
        );
    } else {
      return this.http
        .get(`${environment.apiUrl}/assets/app/core/base.json`)
        .pipe(
          tap((config: any) => {
            Object.assign(coreConfig, config);
          })
        )
        .toPromise()
        .then(
          (config: ICoreConfig) => {
            this.state.config = config;
            this.apiService.configLoadDone$.next(true);
            this.initTheme();
          },
          () => {
            console.log('base json not found!');
          }
        );
    }
  }

  @action
  initTheme(): void {
    if (this.storage.retrieve(this.MODE)) {
      this.state.defTheme = this.storage.retrieve(this.MODE);
      this.setBodyClasses(this.state.defTheme);
    } else {
      this.state.defTheme = this.config.defaultTheme || 'light-theme';
      this.setBodyClasses(this.state.defTheme);
    }
  }

  @action
  setPageContent(): void {
    if (environment.production) {
      const landingPath = '/api/v1/landingPage?content=';
      this.http
        .get<any>(`${environment.apiUrl}${landingPath}${this.apiPath}`)
        .subscribe(
          (pageValue: IPage) => {
            this.updatePage(pageValue);
          },
          (error) => {
            this.setPageNotFound(`${environment.apiUrl}${landingPath}404`);
          }
        );
    } else {
      this.http
        .get<any>(`${environment.apiUrl}/assets/app${this.apiPath}.json`)
        .subscribe(
          (pageValue: IPage) => {
            this.updatePage(pageValue);
          },
          (error) => {
            this.setPageNotFound(`${environment.apiUrl}/assets/app/404.json`);
          }
        );
    }
  }
}

import { Injectable } from '@angular/core';
import { Params } from '@angular/router';
import { isArray, remove, result } from 'lodash-es';
@Injectable()
export abstract class BaseComponent {
  abstract content: any;

  constructor() {}

  getParams(obj: any, key: string): any {
    return obj.params && obj.params[key];
  }

  getValue(obj: any, path: string, key: string): any {
    return obj[path] && obj[path][key];
  }

  getDeepValue(obj: any, path: string): any {
    return result(obj, path);
  }

  row(content: any, padding = '4rem'): string {
    return `0 0 calc(100% / ${content.row || 3} - ${padding})`;
  }

  initFormValueWithUrlQuery(query: any, controls: any[]): any {
    return controls.map((control) => {
      if (control.key in query) {
        control.value = query[control.key];
        return control;
      } else {
        return control;
      }
    });
  }

  // For drupal view json api
  getApiParams(state: any): any {
    const params: string[] = [];
    if (state) {
      Object.keys(state).forEach((key) => {
        const val = state[key];
        if (val) {
          if (isArray(val)) {
            const final = remove(val, (item) => item !== undefined);
            if (final.length > 0) {
              params.push(`${key}=${final.join('+')}`);
            } else {
              return;
            }
          } else {
            params.push(`${key}=${val}`);
          }
        }
      });
    }
    return params.join('&');
  }

  getFormParams(state: any): Params {
    const formParams: any = {};
    if (state) {
      Object.keys(state).forEach((key) => {
        const val = state[key];
        if (val !== '') {
          if (isArray(val)) {
            if (val.length > 0) {
              formParams[key] = val.join('+');
            } else {
              delete formParams[key];
            }
          } else {
            formParams[key] = val;
          }
        } else {
          delete formParams[key];
        }
      });
    }
    return formParams;
  }

  getParamsState(formValues: any, options: any): any {
    return Object.assign({}, formValues, options);
  }

  getUrlQuery(formValues: any, options: any = {}): any {
    const state = this.getParamsState(formValues, options);
    const query: Params = this.getFormParams(state);
    return query;
  }

  handlerPager(pager: any): any {
    if (pager.current_page === null && pager.total_pages === 0) {
      return {
        itemsPerPage: pager.total_items,
        currentPage: 0,
        totalItems: pager.total_items,
      };
    } else {
      return {
        itemsPerPage: pager.items_per_page,
        currentPage: (pager.current_page || 0) + 1,
        totalItems: pager.total_items,
      };
    }
  }
}

