import { Injectable } from '@angular/core';
import * as CryptoJS from 'crypto-js';

@Injectable({
  providedIn: 'root',
})
export class CryptoJSService {
  secretKey = 'xinshi';
  constructor() {}

  // Generate 32-bit random string
  randomString(len: number): string {
    len = len || 32;
    const $chars = 'ABCDEFGHJKMNPQRSTWXYZabcdefhijkmnprstwxyz2345678';
    const maxPos = $chars.length;
    let pwd = '';
    for (let i = 0; i < len; i++) {
      pwd += $chars.charAt(Math.floor(Math.random() * maxPos));
    }
    return pwd;
  }

  encrypt(value: string): string {
    return CryptoJS.AES.encrypt(value, this.secretKey.trim()).toString();
  }

  decrypt(textToDecrypt: string): string {
    return CryptoJS.AES.decrypt(textToDecrypt, this.secretKey.trim()).toString(
      CryptoJS.enc.Utf8
    );
  }
}

import { Inject, Injectable } from '@angular/core';
import { Subject } from 'rxjs';
import { MatDialog } from '@angular/material/dialog';
import { DialogComponent } from '@uiux/widgets/dialog/dialog.component';
import { TextComponent } from '../../uiux/widgets/text/text.component';
import { DOCUMENT } from '@angular/common';

@Injectable({
  providedIn: 'root',
})
export class DialogService {
  dialogState$ = new Subject();
  dialogRef: any;
  constructor(
    private dialog: MatDialog,
    @Inject(DOCUMENT) private doc: Document
  ) {}

  closeDialog(): void {
    this.dialogState$.next(false);
  }

  forceDialog(config: any): void {
    const first = this.doc.referrer;
    const path = this.doc.location.pathname;
    const params = config.params;
    if (!config) {
      return;
    }

    if (params.first && !first) {
      if (params.path) {
        if (path.includes(params.path)) {
          this.openDialog(config);
        }
      } else {
        this.openDialog(config);
      }
      return;
    } else {
      if (params.path) {
        if (path.includes(params.path)) {
          this.openDialog(config);
        }
        return;
      }
    }

    this.openDialog(config);
  }

  openDialog(config: any): void {
    this.dialogRef = this.dialog.open(DialogComponent, {
      width: config.params.width,
      disableClose: true,
      data: {
        renderInputComponent: TextComponent,
        disableCloseButton: true,
        inputData: {
          content: config.text,
        },
      },
    });
  }
}

