import { Injectable, Inject, PLATFORM_ID } from '@angular/core';
import { DOCUMENT, isPlatformBrowser, isPlatformServer } from '@angular/common';

@Injectable({
  providedIn: 'root',
})
export class ScreenService {
  constructor(
    @Inject(DOCUMENT) private document: Document,
    @Inject(PLATFORM_ID) public platformId: object
  ) {}

  isPlatformServer(): boolean {
    return isPlatformServer(this.platformId);
  }

  isPlatformBrowser(): boolean {
    return isPlatformBrowser(this.platformId);
  }

  isElementInViewport(el: any): boolean {
    const rect = el.getBoundingClientRect();
    return (
      rect.top >= 0 &&
      rect.left >= 0 &&
      rect.bottom <=
        (window.innerHeight ||
          this.document.documentElement
            .clientHeight) /* or $(window).height() */ &&
      rect.right <=
        (window.innerWidth ||
          this.document.documentElement.clientWidth) /* or $(window).width() */
    );
  }

  isElementOutTopViewport(el: any): boolean {
    const rect = el.getBoundingClientRect();
    return (
      rect.top < -el.clientHeight &&
      rect.bottom <= 0 /* or $(window).height() */
    );
  }

  isElementOutBottomViewport(el: any): boolean {
    const rect = el.getBoundingClientRect();
    return (
      rect.top >
      (window.innerHeight || this.document.documentElement.clientHeight)
    );
  }

  scrollToAnchor(location: string, wait = 0): void {
    const element = this.document.querySelector('#' + location);
    if (element) {
      setTimeout(() => {
        element.scrollIntoView({
          behavior: 'smooth',
          block: 'start',
          inline: 'nearest',
        });
      }, wait);
    }
  }

  gotoTop(): void {
    window.scroll({
      top: 0,
      left: 0,
      behavior: 'smooth',
    });
  }
}

import { Injectable } from '@angular/core';
import { Subject, Observable, fromEvent } from 'rxjs';
import { debounceTime, distinctUntilChanged, map } from 'rxjs/operators';
import { BreakpointObserver } from '@angular/cdk/layout';
import { action, observable } from 'mobx-angular';
import { MatDrawer } from '@angular/material/sidenav';
import { MediaObserver, MediaChange } from '@angular/flex-layout';
import { ScreenService } from '@core/service/screen.service';

@Injectable({
  providedIn: 'root',
})
export class ScreenState {
  public scroll$ = new Subject();
  public drawer$ = new Subject();
  public stickyMenu$ = new Subject();

  @observable viewPort: string[];
  @observable isDrawer = false;

  constructor(
    public breakpointObserver: BreakpointObserver,
    public mediaObserver: MediaObserver,
    private screenService: ScreenService
  ) {
    if (this.screenService.isPlatformBrowser()) {
      this.initScreen();
      this.initBrowserEvents();
    }
  }

  @action
  initScreen(): any {
    this.mqAlias$().subscribe((mq) => {
      this.viewPort = mq;
    });
  }

  mqAlias$(): Observable<string[]> {
    return this.mediaObserver.asObservable().pipe(
      distinctUntilChanged(
        (x: MediaChange[], y: MediaChange[]) =>
          this.getAlias(x) === this.getAlias(y)
      ),
      map((change: any) => {
        return change.map((item: any) => {
          return item.mqAlias;
        });
      })
    );
  }

  getAlias(change: MediaChange[]): any {
    return change[0].mqAlias;
  }

  eq(targetPoint: string): boolean {
    if (this.screenService.isPlatformBrowser()) {
      return this.viewPort.includes(targetPoint);
    } else {
      // TODO: need confirm
      return 'xs' === targetPoint;
    }
  }

  initBrowserEvents(): void {
    const scroll = fromEvent(window, 'scroll')
      .pipe(debounceTime(200))
      .subscribe(() => {
        this.scroll$.next();
      });
  }

  @action
  toggleDrawer(open: boolean): any {
    this.drawer$.next();
  }

  @action
  updateDrwer(drawer: MatDrawer): void {
    this.isDrawer = drawer.opened;
  }
}

import { Injectable } from '@angular/core';
import { Meta, Title } from '@angular/platform-browser';
import { IPage } from '../mobx/IAppConfig';

@Injectable({
  providedIn: 'root',
})
export class TagsService {
  constructor(private titleService: Title, private meta: Meta) {}

  public setTitle(newTitle: string): void {
    this.titleService.setTitle(newTitle);
  }

  public addMeta(metas: any[]): void {
    this.meta.addTags(metas);
  }

  public updateMeta(meta: any): void {
    this.meta.updateTag(meta);
  }

  public updateTages(pageValue: IPage): void {
    this.setTitle(pageValue.title);
    if (pageValue.meta) {
      pageValue.meta.forEach((item) => {
        this.updateMeta(item);
      });
    } else {
      this.updateMeta({
        name: 'description',
        content: '',
      });
      this.updateMeta({
        name: 'keywords',
        content: '',
      });
    }
  }
}

import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Inject, Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { ApiService } from './api.service';
import { map } from 'rxjs/operators';
import { TokenUser, IUser } from '../mobx/user/IUser';
import { LocalStorageService } from 'ngx-webstorage';
import { CryptoJSService } from './crypto-js.service';
import { CORE_CONFIG } from '@core/token/core.config';
import { ICoreConfig } from '@core/mobx/IAppConfig';
@Injectable({
  providedIn: 'root',
})
export class UserService extends ApiService {
  constructor(
    public http: HttpClient,
    public storage: LocalStorageService,
    public cryptoJS: CryptoJSService,
    @Inject(CORE_CONFIG) private coreConfig: ICoreConfig
  ) {
    super();
  }

  get userApiPath(): string {
    return `${this.apiUrl}${this.coreConfig.apiUrl.userGetPath}`;
  }

  login(userName: string, passWord: string): Observable<any> {
    const httpOptions = {
      headers: new HttpHeaders({
        Accept: 'application/vnd.api+json',
        'Content-type': 'application/vnd.api+json',
      }),
      withCredentials: true,
    };

    return this.http.post<any>(
      `${this.apiUrl}${this.coreConfig.apiUrl.loginPath}?_format=json`,
      {
        name: userName,
        pass: passWord,
      },
      httpOptions
    );
  }

  logout(logoutToken: string): Observable<any> {
    const httpOptions = {
      headers: new HttpHeaders({
        Accept: 'application/vnd.api+json',
      }),
      withCredentials: true,
    };
    const params = ['_format=json', `token=${logoutToken}`].join('&');
    return this.http.post(
      `${this.apiUrl}${this.coreConfig.apiUrl.logoutPath}?${params}`,
      null,
      httpOptions
    );
  }

  getCode(phone: string): Observable<any> {
    const httpOptions = {
      headers: new HttpHeaders({
        'Content-type': 'application/json',
        Accept: 'application/json',
      }),
    };

    return this.http.post(`${this.apiUrl}/api/v1/otp/generate?format=json`, {
      mobile_number: phone,
    });
  }

  loginByPhone(phone: number, code: string): Observable<any> {
    const httpOptions = {
      headers: new HttpHeaders({
        'Content-type': 'application/json',
        Accept: 'application/json',
      }),
    };

    return this.http.post(`${this.apiUrl}/api/v1/otp/login?format=json`, {
      mobile_number: phone,
      code,
    });
  }

  getUserById(id: string, crsfToken: string): Observable<any> {
    const params = [
      `filter[drupal_internal__uid]=${id}`,
      `include=user_picture,roles`,
      `jsonapi_include=1`,
    ].join('&');
    return this.http.get<any>(
      `${this.userApiPath}?${params}`,
      this.optionsWithCookieAndToken(crsfToken)
    );
  }

  getUser(params: string): Observable<any> {
    return this.http.get<any>(`${this.userApiPath}?${params}`);
  }

  getCurrentUserProfile(crsfToken: string): Observable<any> {
    return this.http.get<any>(
      `${this.apiUrl}/api/v1/accountProfile`,
      this.optionsWithCookieAndToken(crsfToken)
    );
  }

  getCurrentUserById(uid: string, token: string): Observable<any> {
    const params = [
      `filter[drupal_internal__uid]=${uid}`,
      `include=user_picture`,
    ].join('&');
    return this.http
      .get<any>(
        `${this.userApiPath}?${params}`,
        this.optionsWithCookieAndToken(token)
      )
      .pipe(
        map((res: any) => {
          const detail = res.data[0];
          const info = detail.attributes;
          const relate = res.included && res.included[0];
          return {
            id: detail.id,
            display_name: info.display_name,
            mail: info.mail,
            authenticated: true,
            picture: relate ? relate.attributes.uri.url : null,
          };
        })
      );
  }

  storeLocalUser(user: IUser): void {
    this.storage.store(
      this.localUserKey,
      this.cryptoJS.encrypt(JSON.stringify(user))
    );
  }

  getLoginState(): Observable<boolean> {
    const httpOptions = {
      headers: new HttpHeaders({
        'Content-type': 'application/json',
      }),
      withCredentials: true,
    };
    return this.http
      .get<any>(`${this.apiUrl}/user/login_status?_format=json`, httpOptions)
      .pipe(
        map((state) => {
          if (state) {
            return true;
          }
          return false;
        })
      );
  }
}

import { Injectable } from '@angular/core';
import { action, observable, computed } from 'mobx-angular';
import { LocalStorageService } from 'ngx-webstorage';
import { IUser, TokenUser } from './IUser';
import { of, Subject, forkJoin } from 'rxjs';
import { UserService } from '@core/service/user.service';
import { UtilitiesService } from '@core/service/utilities.service';
import { CryptoJSService } from '@core/service/crypto-js.service';
import { catchError, map, switchMap } from 'rxjs/operators';
import { HttpHeaders } from '@angular/common/http';
import { environment } from 'src/environments/environment';

const unauthUser = {
  id: '',
  authenticated: false,
  csrf_token: '',
  current_user: {
    uid: '',
    name: '',
    roles: [],
  },
  logout_token: '',
};

@Injectable({
  providedIn: 'root',
})
export class UserState {
  @observable private user: IUser = unauthUser;
  @observable public error = '';
  @observable public loading = false;

  user$ = new Subject<IUser>();

  constructor(
    private cryptoJS: CryptoJSService,
    private userService: UserService,
    private utilities: UtilitiesService,
    private storage: LocalStorageService
  ) {
    if (this.storage.retrieve(this.userService.localUserKey)) {
      this.user = JSON.parse(
        this.cryptoJS.decrypt(
          this.storage.retrieve(this.userService.localUserKey)
        )
      );
      console.log(this.user);
    }
  }

  @computed get currentUser(): IUser {
    return Object.assign({}, this.user);
  }

  @computed
  get authenticated(): boolean {
    return this.user.authenticated;
  }

  @computed
  get picture(): any {
    return this.user && this.user.picture;
  }

  @computed
  get roles(): string[] {
    return (this.currentUser && this.currentUser.current_user.roles) || [''];
  }

  get defaultAvatar(): string {
    return '/assets/images/avatar/default.svg';
  }

  @computed
  get logoutToken(): string {
    return this.currentUser && this.currentUser.logout_token;
  }

  @computed
  get csrfToken(): string {
    return this.currentUser && this.currentUser.csrf_token;
  }

  @action
  login(userName: string, passWord: string): any {
    this.loading = true;
    this.userService.login(userName, passWord).subscribe(
      (data) => {
        this.updateUser(data);
      },
      (error) => {
        this.loading = false;
        this.error = error.message;
      }
    );
  }

  @action
  loginByPhone(phone: number, code: string): any {
    this.loading = true;
    this.userService.loginByPhone(phone, code).subscribe(
      (data) => {
        if (data.status) {
          this.updateUser(data);
        } else {
          this.utilities.openSnackbar(data.message);
          this.loading = false;
        }
      },
      (error) => {
        console.log(error);
      }
    );
  }

  @action
  logout(): any {
    if (environment.drupalProxy) {
      this.logoutUser();
      window.location.href = '/user/logout';
      return;
    }
    this.userService
      .logout(this.logoutToken)
      .pipe(
        catchError((error) => {
          if (error.status === 403) {
            return of(true);
          }
          console.log('');
          return of(false);
        })
      )
      .subscribe(() => {
        this.logoutUser();
      });
  }

  logoutUser(): void {
    this.user$.next(unauthUser);
    this.user = unauthUser;
    this.storage.clear(this.userService.localUserKey);
  }

  @action
  loginUser(data: any, user: any): void {
    this.loading = false;
    this.user$.next(user);
    this.user = Object.assign(data, user);
    this.userService.storeLocalUser(this.user);
  }

  @action
  logouLocalUser(): void {
    this.user$.next(unauthUser);
    this.user = unauthUser;
    this.storage.clear(this.userService.localUserKey);
  }

  @action
  updateUser(data: TokenUser): any {
    this.userService
      .getCurrentUserById(data.current_user.uid, data.csrf_token)
      .subscribe((user) => {
        this.loginUser(data, user);
      });
  }

  @action
  updateUserBySession(): void {
    const options = {
      headers: new HttpHeaders({
        Accept: 'application/vnd.api+json',
        'Content-Type': 'application/vnd.api+json',
      }),
      withCredentials: true,
    };
    const sesstion = this.userService.http.get('/session/token', {
      responseType: 'text',
    });
    const profile = this.userService.http.get(
      '/api/v1/accountProfile',
      options
    );
    let tokenUser = {};
    forkJoin({
      csrf_token: sesstion,
      current_user: profile,
    })
      .pipe(
        switchMap((data: any) => {
          console.log(data);
          tokenUser = data;
          return this.userService.getCurrentUserById(
            data.current_user.uid,
            data.csrf_token
          );
        }),
        catchError((error: any) => {
          console.log(error);
          return of(null);
        })
      )
      .subscribe((user) => {
        console.log('get session user done!');
        this.loginUser(tokenUser, user);
      });
  }

  @action
  refreshLocalUser(user: IUser): void {
    this.user$.next(user);
    this.user = user;
    this.userService.storeLocalUser(user);
  }
}

import { DOCUMENT } from '@angular/common';
import { Inject, Injectable } from '@angular/core';
import { MatSnackBar, MatSnackBarConfig } from '@angular/material/snack-bar';

@Injectable({
  providedIn: 'root',
})
export class UtilitiesService {
  constructor(
    private snackbar: MatSnackBar,
    @Inject(DOCUMENT) private document: Document
  ) {}

  getIndexTitle(title: string): string {
    return title.substring(0, 1);
  }

  get fullYear(): number {
    return new Date().getFullYear();
  }

  openSnackbar(message: string, action = '', config?: MatSnackBarConfig): void {
    this.snackbar.open(
      message,
      action,
      config
        ? config
        : {
            horizontalPosition: 'center',
            verticalPosition: 'top',
            duration: 3000,
          }
    );
  }

  loadScript(src: string, id?: any, defer?: boolean): any {
    const script = this.document.createElement('script');
    script.src = src;
    script.async = true;
    if (defer) {
      script.defer = true;
    }
    if (id) {
      script.id = id;
    }
    this.document.body.appendChild(script);
  }

  getFileType(href: string): string {
    const url = href.toLowerCase();
    const pdfReg = /^.+(\.pdf)$/;
    const txtReg = /^.+(\.txt)$/;
    const wordReg = /^.+(\.doc|\.docx)$/;
    const excelReg = /^.+(\.xls|\.xlsx)$/;
    const jpgReg = /^.+(\.png|\.jpg|\.jpeg|\.bmp)$/;
    if (pdfReg.test(url)) {
      return 'pdf';
    }
    if (txtReg.test(url)) {
      return 'txt';
    }
    if (wordReg.test(url)) {
      return 'word';
    }
    if (excelReg.test(url)) {
      return 'excel';
    }
    if (jpgReg.test(url)) {
      return 'picture';
    }
    return '';
  }
}

