        top: 50%;
        position: absolute;
        width: 100%;
        z-index: 1;
        opacity: 0;
        @include translateAndScale(-50%, -50%, 1.2);
        @include border-radius(100%, 100%, 100%, 100%);
        transition: all 0.4s ease;
      }
    }
    .box-image {
      margin-bottom: 35px;
    }
    &:hover {
      .box-icon {
        &:before {
          @include translateAndScale(-50%, -50%, 1);
          @include transition(all, 0.4, ease);
          opacity: 1;
        }
      }
    }
  }

  &.style-v3 {
