  &.style-v6,
  &.style-v7 {
    .box-icon {
      margin-bottom: 25px;
      margin-left: auto;
      margin-right: auto;
    }
    .box-content-wrapper {
      text-align: center;
      &:after {
        content: "";
        display: table;
        clear: both;
      }
    }
    @media #{$gt-sm} {
      display: flex;
      align-items: flex-start;
      .box-icon {
        margin: 0px 25px 25px 0px;
        flex: 0 0 auto;
      }
      .box-content-wrapper {
        text-align: left;
      }
    }
  }

  &.style-v5 {
    .box-icon {
      @include border-radius(3px, 3px, 3px, 3px);
    }
    &.box-left {
      .box-content-wrapper {
        margin-left: 75px;
      }
    }
    &.box-right {
      .box-content-wrapper {
        margin-right: 75px;
      }
    }
  }

  &.style-v6,
  &.style-v7,
  &.style-v9 {
    .box-content {
      padding-bottom: 0;
    }
    .read-more {
      a {
        display: inline-block;
        font-size: 14px;
        margin-top: 14px;
        position: relative;
        padding-left: 14px;
        span {
          &:before,
          &:after {
            content: "";
            display: block;
            height: 6px;
            left: 0;
            position: absolute;
            top: 7px;
            @include transform-rotate(45deg);
            width: 2px;
          }
          &:after {
            @include transform-rotate(-45deg);
            top: 10px;
          }
        }
      }
    }
  }

  &.style-v8,
  &.style-v10 {
    display: flex;
    padding-bottom: 20px;
    .box-icon {
      flex: 0 0 auto;
      display: flex;
      align-items: center;
      justify-content: center;
      width: 45px;
      height: 45px;
      border-radius: 50%;
      margin-right: 1rem;
      margin-bottom: 1rem;
      ::ng-deep {
        ::ng-deep .mat-icon {
          font-size: 24px;
        }
      }
    }
    .box-content {
      padding-bottom: 0;
    }
  }

  &.style-v10 {
    @media #{$gt-sm} {
      text-align: right;
      .box-icon {
        margin-left: 1rem;
        order: 2;
      }
    }
  }

  // feature absolute
  &.style-v9 {
    border-radius: 10px;
    text-align: center;
    padding: 1.5rem;
    transition: all 0.5s ease;
    .box-icon {
      height: 80px;
      margin: 0 auto;
      border-radius: 6px;
      display: flex;
      justify-content: center;
      align-items: center;
      ::ng-deep .mat-icon {
        font-size: 24px;
      }
    }
    .box-content-wrapper {
      margin-top: 2rem;
    }
  }
}

