@Component({
  selector: 'app-dynamic-card-list1v1',
  templateUrl: './dynamic-card-list1v1.component.html',
  styleUrls: ['./dynamic-card-list1v1.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class DynamicCardList1v1Component
  extends BaseComponent
  implements OnInit
{
  @Input() content: any;

  page: number;
  pager: any;
  loading = false;
  nodes: any[];
  constructor(
    public nodeService: NodeService,
    public routerService: RouteService,
    private screenService: ScreenService,
    private cd: ChangeDetectorRef
  ) {
    super();
  }

  ngOnInit(): void {
    if (this.screenService.isPlatformBrowser()) {
      this.nodeSearch({});
    }
  }

  nodeSearch(options: any): void {
    this.loading = true;
    const state = this.getParamsState({}, options);
    const params = this.getApiParams(state);
    this.nodeService
      .search(this.getParams(this.content, 'type'), params)
      .subscribe(
        (data) => {
          this.updateList(data);
          this.loading = false;
          this.cd.detectChanges();
        },
        (error) => {
          console.log(error);
          this.loading = false;
          this.cd.detectChanges();
        }
      );
  }

  updateList(data: any): void {
    this.pager = this.handlerPager(data.pager);
    this.nodes = data.rows.map((item: any) => {
      return {
        link: {
          label: item.title,
          href: item.url,
        },
        user: item.user,
        time: item.date,
        feature: {
          fullIcon: 'fullscreen',
          openIcon: 'open_in_new',
          link: item.url,
          ratios: this.content.ratios || 'media-4-3',
          img: {
            classes: 'object-fit',
            src: item.image,
            title: item.title,
          },
        },
        moreLabel: '',
      };
    });
    this.cd.detectChanges();
  }

  onPageChange(page: number): void {
    this.page = page - 1;
    this.nodeSearch({ page: this.page });
  }

  trackByFn(index: number, item: any): number {
    return index;
  }
}

import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  Input,
  OnInit,
} from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { FormGroup } from '@angular/forms';
import { isEmpty, omitBy, result } from 'lodash-es';
import { NodeService } from '@core/service/node.service';
import { RouteService } from '@core/service/route.service';
import { BaseComponent } from '@uiux/base/base.widget';
import { FormService } from '@core/service/form.service';
import { debounceTime, distinctUntilChanged } from 'rxjs/operators';
import { ScreenService } from '@core/service/screen.service';

@Component({
  selector: 'app-dynamic-card-list',
  templateUrl: './dynamic-card-list.component.html',
  styleUrls: ['./dynamic-card-list.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class DynamicCardListComponent extends BaseComponent implements OnInit {
  @Input() content: any;
  keys: string;
  page: number;
  pager: any;
  form: FormGroup;
  filterForm: any[];
  nodes: any[];
  status: any;
  loading = false;

  constructor(
    public nodeService: NodeService,
    private router: ActivatedRoute,
    public routerService: RouteService,
    private formService: FormService,
    private screenService: ScreenService,
    private cd: ChangeDetectorRef
  ) {
    super();
  }

  ngOnInit(): void {
    if (this.screenService.isPlatformBrowser()) {
      this.router.queryParams.subscribe((query: any) => {
        this.page = query.page || 0;
        const queryOpt = omitBy(
          Object.assign(
            {
              page: this.page,
            },
            query
          ),
          isEmpty
        );
        if (this.content.sidebar) {
          this.filterForm = this.initFormValueWithUrlQuery(
            queryOpt,
            this.content.sidebar
          );
          this.initForm(this.filterForm);
        }
        this.nodeSearch(queryOpt);
      });
    }
  }

  initForm(items: any[]): void {
    this.form = this.formService.toFormGroup(items);
    this.form.valueChanges
      .pipe(debounceTime(1000), distinctUntilChanged())
      .subscribe((value) => {
        const params = Object.assign({ page: 0 }, value);
        this.onSelectChange(params);
      });
  }

  nodeSearch(options: any): void {
    this.loading = true;
    const state = this.getParamsState(this.form?.value, options);
    const params = this.getApiParams(state);
    this.nodeService
      .search(this.getParams(this.content, 'type'), params)
      .subscribe(
        (data) => {
          this.updateList(data, this.form.value, options);
          this.loading = false;
          this.cd.detectChanges();
        },
        (error) => {
          this.loading = false;
          this.cd.detectChanges();
        }
      );
  }

  onSelectChange(options: any): void {
    this.page = options.page;
    this.nodeSearch(options);
  }

  onPageChange(page: any): void {
    this.page = page - 1;
    this.nodeSearch({ page: this.page });
  }

  updateList(data: any, formValues: any, options: any): void {
    this.pager = this.handlerPager(data.pager);
    this.nodes = data.rows.map((item: any) => {
      const link = item.url;
      const title = result(
        item,
        this.getValue(this.content, 'fields', 'title')
      );
      const subTitle = result(
        item,
        this.getValue(this.content, 'fields', 'subTitle')
      );
      const body = result(item, this.getValue(this.content, 'fields', 'body'));
      return {
        link: {
          label: title,
          href: link,
        },
        subTitle,
        classes: this.content.shadow ? '' : 'card-no-shadow',
        feature: {
          fullIcon: 'fullscreen',
          openIcon: 'open_in_new',
          link,
          ratios: this.content.ratios || 'media-4-3',
          img: {
            classes: 'object-fit',
            src: item.image,
            alt: title,
          },
        },
        body,
      };
    });
    this.routerService.updateQueryParams(this.getUrlQuery(formValues, options));
    this.cd.detectChanges();
  }

  trackByFn(index: number, item: any): number {
    return index;
  }
}

