import { FormGroup } from '@angular/forms';
import { IAction, IActionParams } from '@core/interface/IForm';
import { UserState } from '@core/mobx/user/UserState';
import { DialogService } from '@core/service/dialog.service';
import { FormService } from '@core/service/form.service';
import { NodeService } from '@core/service/node.service';
import { UtilitiesService } from '@core/service/utilities.service';
import { Router } from '@angular/router';
import { ScreenService } from '@core/service/screen.service';
import { Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';
import { IControl } from '@core/interface/widgets/IControl';

@Component({
  selector: 'app-dynamic-form',
  templateUrl: './dynamic-form.component.html',
  styleUrls: ['./dynamic-form.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class DynamicFormComponent implements OnInit, OnDestroy {
  @Input() content: IControl[];
  @Input() actions: IAction[];
  loading = false;
  form: FormGroup;

  destroy$: Subject<boolean> = new Subject<boolean>();
  constructor(
    private cd: ChangeDetectorRef,
    private dialogService: DialogService,
    private formService: FormService,
    private nodeService: NodeService,
    private userState: UserState,
    private utilitiesService: UtilitiesService,
    private router: Router,
    private screenService: ScreenService
  ) {}

  ngOnInit(): void {
    if (this.screenService.isPlatformBrowser()) {
      this.form = this.formService.toFormGroup(this.content);
    }
  }

  onClick(params: IActionParams): void {
    this.loading = true;
    this.nodeService
      .addNode(params.type, this.form.value, this.userState.csrfToken)
      .pipe(takeUntil(this.destroy$))
      .subscribe(
        (res) => {
          const link = this.nodeService.getNodePath(res.data.attributes);
          this.loading = false;
          this.utilitiesService.openSnackbar(params.snackMes || '');
          setTimeout(() => {
            this.dialogService.closeDialog();
            this.router.navigate([link]);
          }, 2000);
          this.cd.detectChanges();
        },
        () => {
          this.loading = false;
          console.log('Please check user state.');
          this.cd.detectChanges();
        }
      );
  }

  ngOnDestroy(): void {
    this.destroy$.next(true);
    this.destroy$.complete();
  }
}

