})
export class MapListV1Component implements OnInit {
  @Input() content: any;
  loading: boolean;
  selectedId: number;
  constructor(private amapState: AMapState) {}

  ngOnInit(): void {}
  onClick(event: IMark): void {
    const obj: IMark = {
      index: event.index,
      marker: this.markerTem(event.item),
    };
    this.amapState.markers$.next(obj);
  }

  markerTem(item: any): any {
    return `
