import {
  Component,
  OnInit,
  OnDestroy,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Inject,
} from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from '@core/service/user.service';
import { UserState } from '@core/mobx/user/UserState';
import { ScreenService } from '@core/service/screen.service';
import { isEmpty } from 'lodash-es';
import { CORE_CONFIG } from '@core/token/core.config';
import { ICoreConfig } from '@core/mobx/IAppConfig';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class UserComponent implements OnInit, OnDestroy {
  user: any;
  id: any;
  constructor(
    private cd: ChangeDetectorRef,
    private route: Router,
    private screenService: ScreenService,
    private userService: UserService,
    private userState: UserState,
    @Inject(CORE_CONFIG) private coreConfig: ICoreConfig
  ) {}

  ngOnInit(): void {
    if (this.screenService.isPlatformBrowser()) {
      this.getUser();
      this.userState.user$.subscribe((user) => {
        if (!user.authenticated) {
          setTimeout(() => {
            this.route.navigate(['user/login']);
          }, 2000);
        } else {
          window.location.reload();
        }
      });
    }
  }

  getUser(): any {
    const people = {};
    this.userService
      .getUserById(
        this.userState.currentUser.current_user.uid,
        this.userState.csrfToken
      )
      .subscribe((res) => {
        const info = res.data[0];
        if (!info) {
          return;
        }
        const profile = {
          bannerBg: this.getBanner(),
          avatar: {
            src: info?.user_picture?.uri?.url || this.userState.defaultAvatar,
            alt: info.name,
          },
          name: info.name,
          subTitle: info.display_name,
          details: {
            label: '',
            elements: [
              {
                icon: {
                  color: 'warn',
                  svg: 'arrow_right',
                  inline: true,
                },
                label: '',
                content: info.mail || '',
              },
              {
                icon: {
                  color: 'warn',
                  svg: 'arrow_right',
                  inline: true,
                },
                label: '',
                content: info.phone_number || '',
              },
              {
                icon: {
                  color: 'warn',
                  svg: 'arrow_right',
                  inline: true,
                },
                label: '',
                content: this.getRoles(info.roles),
              },
            ],
          },
        };
        this.user = Object.assign(people, profile);
        this.cd.detectChanges();
      });
  }

  getBanner(): any {
    return {
      classes: 'bg-fill-width overlay overlay-80',
      img: {
        hostClasses: 'bg-center',
        src:
          this.coreConfig?.user?.banner ||
          '/assets/images/16-9/business-14.jpeg',
        alt: 'page title',
      },
    };
  }

  getRoles(roles: any): string[] {
    if (!roles || isEmpty(roles.data)) {
      return [''];
    }
    return roles.map((rule: any) => {
      return rule.label;
    });
  }

  logout(): void {
    this.userState.logout();
  }

  ngOnDestroy(): void {}
}

